ItemBlogs itemBlogsFromJson(String str) => ItemBlogs.fromJson(json.decode(str));
String itemBlogsToJson(ItemBlogs data) => json.encode(data.toJson());
class ItemBlogs {
  ItemBlogs({
      String? blogId, 
      String? title, 
      String? slug, 
      String? category, 
      String? publishedBy, 
      String? publishedDate, 
      String? publishedDateView, 
      String? discription, 
      String? isActive, 
